angular.module('teachur')
.factory('CurrentUser', CurrentUser);

function CurrentUser($http, $q, $rootScope) {

  this.toggleComplete = function (itemId) {
    var _this = this;

    return $q(function(resolve, reject) {
      return $http.post('/api/current-user/toggle-complete/' + itemId)
      .success(function(res) {
        return _this.get().then(resolve);
      }).error(function(err) {
        return reject(err);
      });

    });
  };

  this.toggleEnroll = function(itemName, itemId) {
    var _this = this;

    if (_this.isEnrolled(itemName, itemId)) {
      return _this.dropOut(itemName, itemId);
    } else {
      return _this.enroll(itemName, itemId);
    }
  };

  this.enroll = function (itemName, itemId) {
    var _this = this;

    return $q(function(resolve, reject) {
      return $http.post('/api/current-user/enroll/' + itemName + '/' + itemId)
      .success(function(res) {
        return _this.get().then(resolve);
      }).error(function(err) {
        return reject(err);
      });

    });
  };

  this.dropOut = function (itemName, itemId) {
    var _this = this;

    return $q(function(resolve, reject) {
      return $http.post('/api/current-user/drop-out/' + itemName + '/' + itemId)
      .success(function(res) {
        return _this.get().then(resolve);
      }).error(function(err) {
        return reject(err);
      });

    });
  };

  this.get = function() {

    return $q(function(resolve, reject) {
      return $http.get('/api/current-user')
      .success(function(user) {
        localStorage.user = JSON.stringify(user);
        $rootScope.user = user;
        return resolve(user);
      }).error(function(err) {
        return reject(err);
      });

    });
    
  };

  this.isEnrolled = function(itemName, itemId) {
    if (!$rootScope.user) return false;
    var items = itemName === 'degree' ? $rootScope.user.enrolledDegrees : $rootScope.user.enrolledCourses;
    return _.findIndex(items, { _id: itemId }) !== -1;
  };

  this.isCompleted = function(itemId) {
    if (!$rootScope.user) return false;
    return _.findIndex($rootScope.user.completedObjectives, { _id: itemId }) !== -1;
  };

  this.isMandatoryCourse = function(itemId) {
    if (!$rootScope.user) return false;
    return _.findIndex($rootScope.user.mandatoryCourses, { _id: itemId }) !== -1;
  };

  this.getCompletionStatus = function(itemId) {
    if (!$rootScope.user) return false;
    return $rootScope.user.completionStatuses[itemId];
  };

  this.calculateLessonCompletion = function(lesson, course) {
    if (this.isEnrolled('course', course._id)) {
      var completedObjectivesForLesson = _.intersection(lesson.components, _.map($rootScope.user.completedObjectives, '_id'));
      var status = completedObjectivesForLesson.length / lesson.components.length;

      return _.round(status * 100, 0);
    }
  };

  return this;
}
;
angular.module('teachur')
.service('FilesService', FilesService);

function FilesService($http, $q) {
  var _this = this;
  this.promises = [];

  function isVideo(filename) {
    if (!filename) {
      return false;
    }
    var ext = _this.getExtension(filename);
    switch (ext.toLowerCase()) {
    case 'm4v':
    case 'avi':
    case 'mpg':
    case 'mp4':
        // etc
        return true;
    }
    return false;
  }

  function isAudio(filename) {
    if (!filename) {
      return false;
    }
    var ext = _this.getExtension(filename);
    switch (ext.toLowerCase()) {
    case 'mp3':
        // etc
        return true;
    }
    return false;
  }

  this.uploadAssessmentsFiles = function(assessments, newAssessments) {
    var _this = this;
    _this.promises = [];

    assessments.forEach(function(assessment, i) {
      if (!assessment._id) {
        assessment._id = newAssessments[i]._id;
      }
      
      _this.populatePromises(assessment.files, assessment, 'assessment');
    });

    return $q.all(_this.promises);
  };

  this.uploadObjectiveFiles = function(objective) {
    var _this = this;
    _this.promises = [];
    _this.populatePromises(objective.recommendedMedia, objective, 'objective');
    return $q.all(_this.promises);
  };

  this.populatePromises = function(files, item, itemName) {
    var _this = this;
    files.forEach(function(file) {
      if (file && !file.external && file.newFile) {
        var fd = new FormData();
        fd.append('file', file);
        _this.promises.push($http.post('/api/uploads/' + itemName + '/' + item._id + '/file/' + file._id, fd, {transformRequest: angular.identity, headers: {'Content-Type': undefined} }));
      }
    });
  };

  this.isVideo = function(filename) {
    return isVideo(filename);
  };

  this.isAudio = function(filename) {
    return isAudio(filename);
  };

  this.getExtension = function(filename){
    var parts = filename.split('.');
    return parts[parts.length - 1];
  }

  return this;
};
;
angular.module('teachur')
.service('Interests', Interests);

function Interests($http, $q) {
  this.urlMap = {
    'personal': '/api/personal-interests',
    'academic': '/api/interests',
  };

  this.interests = [];

  this.getAll = function(type) {
    var _this = this;
    
    return $q(function(resolve, reject) {
      return $http.get(_this.urlMap[type])
      .success(function(res) {
        _this.interests = res;
        return resolve(res);
      }).error(function(err) {
        return reject(err);
      });

    });

  };

  this.create = function(interest, type) {
    var _this = this;
    
    return $q(function(resolve, reject) {
      if (interest._id) {
        return resolve(interest);
      }

      return $http.post(_this.urlMap[type], interest)
      .success(function(res) {
        _this.interests = res;
        return resolve(res);
      }).error(function(err) {
        return reject(err);
      });

    });
  };

  this.search = function(type, query) {
    var _this = this;
    return $http.get(_this.urlMap[type] + '/search?searchText=' + query).success(function(data) {
      return data;
    });
  };

  return this;
};
